// src/components/sections/Services.tsx
import React, { useEffect, useState, useRef } from 'react'

// Service data
const residentialServices = [
  {
    title: 'Pantry Organization',
    description: 'Product sourcing, workflow optimization, declutter food, boxes, add containers',
    icon: '🥫',
  },
  {
    title: 'Kitchen Organization',
    description: 'Cabinets, drawers, under-sink storage optimization',
    icon: '🍴',
  },
  {
    title: 'Closet Makeovers',
    description: 'Sorting, folding, seasonal rotation, space-saving strategies',
    icon: '👔',
  },
  {
    title: 'Home Office Setup',
    description: 'Desks, bookcases, staging, décor, paper filing systems',
    icon: '💼',
  },
  {
    title: 'Playroom Organization',
    description: 'Toy rotation systems, labeled bins for easy cleanup',
    icon: '🧸',
  },
  {
    title: 'Laundry Room Setup',
    description: 'Efficient layout and storage solutions',
    icon: '🧺',
  },
  {
    title: 'Moving Services',
    description: 'Declutter before moving and organize in new home',
    icon: '📦',
  },
  {
    title: 'Home Styling',
    description: 'Staging and declutter after organizing',
    icon: '🏡',
  },
  {
    title: 'Maintenance Plans',
    description: 'Monthly or seasonal refresh sessions',
    icon: '📅',
  },
]

const commercialServices = [
  {
    title: 'Office Space Organization',
    description: 'Desks, common areas, conference rooms optimization',
    icon: '🏢',
  },
  {
    title: 'Filing Organization',
    description: 'Physical document organization systems',
    icon: '📁',
  },
  {
    title: 'Supply Room Setup',
    description: 'Inventory and labeling systems',
    icon: '📋',
  },
  {
    title: 'Break Room Organization',
    description: 'Snacks, kitchenware, shared storage solutions',
    icon: '☕',
  },
  {
    title: 'Reception & Lobby',
    description: 'First impression-ready space decluttering',
    icon: '🎯',
  },
  {
    title: 'Workflow Optimization',
    description: 'Functional setup of high-use areas',
    icon: '⚡',
  },
  {
    title: 'Event Prep Support',
    description: 'Organizing supplies/materials before corporate events',
    icon: '🎉',
  },
]

const addOnServices = [
  {
    title: 'Donation Coordination',
    description: 'Drop-off or pickup of unwanted items',
    icon: '♻️',
  },
  {
    title: 'Labeling Services',
    description: 'Custom, aesthetic labeling for jars, bins, files',
    icon: '🏷️',
  },
  {
    title: 'Product Sourcing',
    description: 'Recommending or purchasing containers and tools',
    icon: '🛍️',
  },
  {
    title: 'Virtual Organizing',
    description: 'Guidance via video calls for remote clients',
    icon: '💻',
  },
  {
    title: 'Gift Certificates',
    description: 'Organization as a thoughtful service gift',
    icon: '🎁',
  },
  {
    title: 'Workshops & Coaching',
    description: 'Teach clients how to maintain their spaces',
    icon: '🎓',
  },
]

// Minimalist Service Card with Flip Effect
interface ServiceCardProps {
  service: {
    title: string
    description: string
    icon?: string
  }
  index: number
  isVisible: boolean
  category: 'residential' | 'commercial' | 'addon'
}

const ServiceCard: React.FC<ServiceCardProps> = ({ service, index, isVisible, category }) => {
  const [isFlipped, setIsFlipped] = useState(false)

  // Handle escape key to close flipped card
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isFlipped) {
        setIsFlipped(false)
      }
    }

    if (isFlipped) {
      document.addEventListener('keydown', handleEscape)
      return () => document.removeEventListener('keydown', handleEscape)
    }
  }, [isFlipped])

  // Category accent colors
  const categoryAccents = {
    residential: {
      front: 'bg-gradient-to-br from-sage-light/5 to-mint-accent/5',
      back: 'bg-gradient-to-br from-sage-light/10 to-mint-accent/10',
      accent: 'text-sage-light',
      border: 'border-sage-light/20'
    },
    commercial: {
      front: 'bg-gradient-to-br from-forest-shadow/5 to-charcoal/5',
      back: 'bg-gradient-to-br from-forest-shadow/10 to-charcoal/10',
      accent: 'text-forest-shadow',
      border: 'border-forest-shadow/20'
    },
    addon: {
      front: 'bg-gradient-to-br from-gold-highlight/5 to-warm-ivory/5',
      back: 'bg-gradient-to-br from-gold-highlight/10 to-warm-ivory/10',
      accent: 'text-gold-highlight',
      border: 'border-gold-highlight/20'
    }
  }

  const colors = categoryAccents[category]

  // Service-specific details (you can customize these per service)
  const getServiceDetails = (title: string) => {
    const details = {
      'Pantry Organization': {
        duration: '4-6 hours',
        includes: ['Custom labeling system', 'Container recommendations', 'Inventory management'],
        process: 'We sort, categorize, and create zones for easy meal planning'
      },
      'Kitchen Organization': {
        duration: '3-5 hours',
        includes: ['Drawer dividers', 'Cabinet optimization', 'Counter decluttering'],
        process: 'Maximize functionality while maintaining aesthetic appeal'
      },
      'Closet Makeovers': {
        duration: '4-8 hours',
        includes: ['Seasonal rotation system', 'Color coordination', 'Accessory organization'],
        process: 'Transform your closet into a boutique-like experience'
      },
      'Home Office Setup': {
        duration: '3-6 hours',
        includes: ['Filing systems', 'Cable management', 'Supply organization'],
        process: 'Create a workspace that inspires productivity'
      },
      'Office Space Organization': {
        duration: '6-10 hours',
        includes: ['Desk optimization', 'Meeting room setup', 'Storage solutions'],
        process: 'Design efficient workflows for your team'
      },
      'Virtual Organizing': {
        duration: '1-2 hours per session',
        includes: ['Video consultation', 'Custom action plan', 'Follow-up support'],
        process: 'Get expert guidance from the comfort of your home'
      }
    }

    return details[title] || {
      duration: '3-5 hours',
      includes: ['Consultation', 'Custom plan', 'Implementation'],
      process: 'Tailored solution for your specific needs'
    }
  }

  const details = getServiceDetails(service.title)

  return (
    <div 
      className={`relative h-80 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
      }`}
      style={{ 
        transitionDelay: `${index * 100}ms`,
        perspective: '1000px'
      }}
    >
      <div 
        className={`relative w-full h-full transition-transform duration-700 cursor-pointer`}
        style={{
          transformStyle: 'preserve-3d',
          transform: isFlipped ? 'rotateY(180deg)' : 'rotateY(0deg)'
        }}
        onClick={() => setIsFlipped(!isFlipped)}
        onTouchEnd={(e) => {
          e.preventDefault()
          setIsFlipped(!isFlipped)
        }}
      >
        {/* Front of Card */}
        <div 
          className={`absolute inset-0 rounded-xl border ${colors.border} ${colors.front} 
                      shadow-lg hover:shadow-xl transition-shadow duration-300 p-8
                      flex flex-col justify-between`}
          style={{
            backfaceVisibility: 'hidden',
            WebkitBackfaceVisibility: 'hidden'
          }}
        >
          <div>
            {/* Icon */}
            {service.icon && (
              <span className="text-4xl block mb-6">
                {service.icon}
              </span>
            )}

            {/* Title */}
            <h4 className="text-xl font-medium text-charcoal mb-3">
              {service.title}
            </h4>

            {/* Description */}
            <p className="text-sm text-forest-shadow/70 leading-relaxed">
              {service.description}
            </p>
          </div>

          {/* Flip Indicator */}
          <div className="flex items-center justify-between mt-6">
            <span className={`text-xs ${colors.accent} font-medium`}>
              {details.duration}
            </span>
            <div className="flex items-center text-charcoal/30">
              <span className="text-xs mr-2">See details</span>
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} 
                      d="M13 5l7 7-7 7M5 5l7 7-7 7" />
              </svg>
            </div>
          </div>
        </div>

        {/* Back of Card */}
        <div 
          className={`absolute inset-0 rounded-xl border ${colors.border} ${colors.back} 
                      shadow-lg p-8 flex flex-col`}
          style={{
            backfaceVisibility: 'hidden',
            WebkitBackfaceVisibility: 'hidden',
            transform: 'rotateY(180deg)'
          }}
        >
          {/* Back Header */}
          <div className="mb-6">
            <h4 className="text-lg font-medium text-charcoal mb-2">{service.title}</h4>
            <div className="h-px bg-charcoal/10"></div>
          </div>

          {/* What's Included */}
          <div className="mb-6">
            <h5 className={`text-xs uppercase tracking-wider ${colors.accent} mb-3`}>
              What's Included
            </h5>
            <ul className="space-y-2">
              {details.includes.map((item, i) => (
                <li key={i} className="flex items-start text-sm text-forest-shadow/80">
                  <span className={`${colors.accent} mr-2 mt-0.5`}>•</span>
                  {item}
                </li>
              ))}
            </ul>
          </div>

          {/* Process */}
          <div className="mb-6">
            <h5 className={`text-xs uppercase tracking-wider ${colors.accent} mb-2`}>
              Our Approach
            </h5>
            <p className="text-sm text-forest-shadow/70 leading-relaxed">
              {details.process}
            </p>
          </div>

          {/* Back Footer */}
          <div className="mt-auto flex items-center justify-between">
            <a 
              href="#contact"
              className={`text-sm ${colors.accent} hover:underline`}
              onClick={(e) => e.stopPropagation()}
            >
              Book this service →
            </a>
            <button className="text-charcoal/30">
              <svg className="w-5 h-5 rotate-180" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} 
                      d="M13 5l7 7-7 7M5 5l7 7-7 7" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

// Horizontal Scroll Section
interface ScrollSectionProps {
  title: string
  subtitle: string
  services: any[]
  category: 'residential' | 'commercial' | 'addon'
  isVisible: boolean
}

const ScrollSection: React.FC<ScrollSectionProps> = ({ title, subtitle, services, category, isVisible }) => {
  const scrollRef = useRef<HTMLDivElement>(null)
  const [showLeftArrow, setShowLeftArrow] = useState(false)
  const [showRightArrow, setShowRightArrow] = useState(true)
  const [scrollProgress, setScrollProgress] = useState(0)

  const handleScroll = () => {
    if (scrollRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = scrollRef.current
      setShowLeftArrow(scrollLeft > 0)
      setShowRightArrow(scrollLeft < scrollWidth - clientWidth - 10)

      // Calculate scroll progress
      const maxScroll = scrollWidth - clientWidth
      const progress = maxScroll > 0 ? (scrollLeft / maxScroll) * 100 : 0
      setScrollProgress(progress)
    }
  }

  const scroll = (direction: 'left' | 'right') => {
    if (scrollRef.current) {
      const scrollAmount = 340 // Card width + gap
      scrollRef.current.scrollBy({
        left: direction === 'left' ? -scrollAmount : scrollAmount,
        behavior: 'smooth'
      })
    }
  }

  useEffect(() => {
    const element = scrollRef.current
    if (element) {
      element.addEventListener('scroll', handleScroll, { passive: true })
      handleScroll()
      return () => element.removeEventListener('scroll', handleScroll)
    }
  }, [])

  return (
    <div className={`mb-24 transition-all duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      {/* Section Header */}
      <div className="mb-8 flex items-end justify-between">
        <div>
          <h3 className="text-2xl font-light text-charcoal mb-2">{title}</h3>
          <p className="text-sm text-forest-shadow/60 mb-1">{subtitle}</p>
          <p className="text-xs text-charcoal/40 italic">Click cards to see details</p>
        </div>

        {/* Scroll Progress Indicator */}
        <div className="hidden lg:block w-32 h-1 bg-gray-200 rounded-full overflow-hidden">
          <div 
            className="h-full bg-charcoal/20 rounded-full transition-all duration-300"
            style={{ width: `${scrollProgress}%` }}
          />
        </div>
      </div>

      {/* Scroll Container */}
      <div className="relative">
        {/* Navigation Arrows */}
        <button
          onClick={() => scroll('left')}
          className={`absolute left-0 top-1/2 -translate-y-1/2 z-10 w-10 h-10 bg-white rounded-full shadow-lg 
                     flex items-center justify-center transition-all duration-300 hover:shadow-xl
                     ${showLeftArrow ? 'opacity-100 -translate-x-5' : 'opacity-0 translate-x-0 pointer-events-none'}`}
        >
          <svg className="w-4 h-4 text-charcoal" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        <button
          onClick={() => scroll('right')}
          className={`absolute right-0 top-1/2 -translate-y-1/2 z-10 w-10 h-10 bg-white rounded-full shadow-lg 
                     flex items-center justify-center transition-all duration-300 hover:shadow-xl
                     ${showRightArrow ? 'opacity-100 translate-x-5' : 'opacity-0 translate-x-0 pointer-events-none'}`}
        >
          <svg className="w-4 h-4 text-charcoal" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>

        {/* Cards Container */}
        <div 
          ref={scrollRef}
          className="flex gap-6 overflow-x-auto scrollbar-hide scroll-smooth pb-4 -mx-4 px-4"
          style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
        >
          {services.map((service, index) => (
            <div key={index} className="flex-none w-80 h-80">
              <ServiceCard 
                service={service} 
                index={index} 
                isVisible={isVisible} 
                category={category}
              />
            </div>
          ))}
          {/* End padding */}
          <div className="flex-none w-4" />
        </div>
      </div>
    </div>
  )
}

// Main Services Component
const Services: React.FC = () => {
  const [sectionsVisible, setSectionsVisible] = useState({
    header: false,
    residential: false,
    commercial: false,
    addons: false,
    cta: false
  })

  useEffect(() => {
    const options = {
      threshold: 0.1,
      rootMargin: '0px 0px -100px 0px'
    }

    const observers = {
      header: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) setSectionsVisible(prev => ({ ...prev, header: true }))
      }, options),
      residential: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) setSectionsVisible(prev => ({ ...prev, residential: true }))
      }, options),
      commercial: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) setSectionsVisible(prev => ({ ...prev, commercial: true }))
      }, options),
      addons: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) setSectionsVisible(prev => ({ ...prev, addons: true }))
      }, options),
      cta: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) setSectionsVisible(prev => ({ ...prev, cta: true }))
      }, options)
    }

    // Observe elements
    const elements = {
      header: document.getElementById('services-header'),
      residential: document.getElementById('services-residential'),
      commercial: document.getElementById('services-commercial'),
      addons: document.getElementById('services-addons'),
      cta: document.getElementById('services-cta')
    }

    Object.entries(elements).forEach(([key, element]) => {
      if (element && observers[key as keyof typeof observers]) {
        observers[key as keyof typeof observers].observe(element)
      }
    })

    return () => {
      Object.values(observers).forEach(observer => observer.disconnect())
    }
  }, [])

  return (
    <section id="services" className="py-24 bg-gradient-to-b from-white to-fog-gray/30">
      <div className="container">
        {/* Elegant Header */}
        <div 
          id="services-header"
          className={`max-w-3xl mb-20 transition-all duration-1000 ${
            sectionsVisible.header ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
          }`}
        >
          <h2 className="text-4xl md:text-5xl font-light text-charcoal mb-6">
            Our Services
          </h2>
          <p className="text-lg text-forest-shadow/70 leading-relaxed">
            From residential sanctuaries to professional workspaces, we create
            organized environments that inspire productivity and peace of mind.
          </p>
        </div>

        {/* Service Sections with Horizontal Scroll */}
        <div id="services-residential">
          <ScrollSection
            title="Residential"
            subtitle="Transform your home into a sanctuary of order and beauty"
            services={residentialServices}
            category="residential"
            isVisible={sectionsVisible.residential}
          />
        </div>

