// src/components/sections/Services.tsx
import React, { useEffect, useState } from 'react'

// Service Card Component (inline for now - can be moved to separate file)
interface ServiceCardProps {
  service: {
    title: string;
    description: string;
    icon?: string;
  };
  index: number;
  category: 'residential' | 'commercial' | 'addon';
}

const ServiceCard: React.FC<ServiceCardProps> = ({ service, index, category }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);

  // Category colors
  const categoryColors = {
    residential: {
      bg: 'bg-sage-light/10',
      border: 'border-sage-light',
      icon: 'bg-sage-light/20',
      text: 'text-sage-light'
    },
    commercial: {
      bg: 'bg-forest-shadow/10',
      border: 'border-forest-shadow',
      icon: 'bg-forest-shadow/20',
      text: 'text-forest-shadow'
    },
    addon: {
      bg: 'bg-gold-highlight/10',
      border: 'border-gold-highlight',
      icon: 'bg-gold-highlight/20',
      text: 'text-gold-highlight'
    }
  };

  const colors = categoryColors[category];

  return (
    <>
      <div 
        className={`relative bg-white rounded-xl shadow-lg hover:shadow-2xl transition-all duration-500 overflow-hidden cursor-pointer transform hover:-translate-y-2 opacity-0 animate-fadeInUp ${colors.bg}`}
        style={{ 
          animationDelay: `${index * 50}ms`,
          animationFillMode: 'forwards'
        }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onClick={() => setIsExpanded(true)}
      >
        {/* Decorative Top Border */}
        <div className={`absolute top-0 left-0 right-0 h-1 ${colors.border.replace('border', 'bg')}`} />

        <div className="p-6">
          {/* Icon */}
          {service.icon && (
            <div className={`w-16 h-16 ${colors.icon} rounded-full flex items-center justify-center mb-4 mx-auto transform transition-transform duration-300 ${isHovered ? 'scale-110 rotate-3' : ''}`}>
              <span className="text-3xl">{service.icon}</span>
            </div>
          )}

          {/* Title */}
          <h4 className="text-xl font-semibold text-charcoal mb-3 text-center">
            {service.title}
          </h4>

          {/* Description */}
          <p className="text-forest-shadow/80 text-center text-sm leading-relaxed">
            {service.description}
          </p>

          {/* Learn More Link */}
          <div className={`mt-4 text-center opacity-0 transform translate-y-2 transition-all duration-300 ${isHovered ? 'opacity-100 translate-y-0' : ''}`}>
            <span className={`inline-flex items-center text-sm font-medium ${colors.text}`}>
              Learn More
              <svg className="ml-1 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </span>
          </div>
        </div>

        {/* Hover Effect Background */}
        <div className={`absolute inset-0 ${colors.bg} opacity-0 transition-opacity duration-300 ${isHovered ? 'opacity-30' : ''}`} />
      </div>

      {/* Expanded Modal */}
      {isExpanded && (
        <div 
          className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4"
          onClick={() => setIsExpanded(false)}
        >
          <div 
            className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto transform scale-95 opacity-0 animate-scaleIn"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Modal Header */}
            <div className={`relative ${colors.bg} p-8 border-b border-gray-100`}>
              <button
                onClick={() => setIsExpanded(false)}
                className="absolute top-4 right-4 p-2 text-charcoal/60 hover:text-charcoal transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>

              <div className="flex items-center space-x-4">
                {service.icon && (
                  <div className={`w-20 h-20 ${colors.icon} rounded-full flex items-center justify-center`}>
                    <span className="text-4xl">{service.icon}</span>
                  </div>
                )}
                <div>
                  <h3 className="text-2xl font-serif text-charcoal">{service.title}</h3>
                  <span className={`text-sm font-medium ${colors.text} capitalize`}>{category} Service</span>
                </div>
              </div>
            </div>

            {/* Modal Content */}
            <div className="p-8">
              <div className="mb-6">
                <h4 className="text-lg font-semibold text-charcoal mb-2">Service Overview</h4>
                <p className="text-forest-shadow leading-relaxed">{service.description}</p>
              </div>

              {/* What's Included */}
              <div className="mb-6">
                <h4 className="text-lg font-semibold text-charcoal mb-3">What's Included</h4>
                <ul className="space-y-2">
                  <li className="flex items-start">
                    <span className="text-gold-highlight mr-2">✓</span>
                    <span className="text-forest-shadow">Initial consultation and space assessment</span>
                  </li>
                  <li className="flex items-start">
                    <span className="text-gold-highlight mr-2">✓</span>
                    <span className="text-forest-shadow">Custom organization plan tailored to your needs</span>
                  </li>
                  <li className="flex items-start">
                    <span className="text-gold-highlight mr-2">✓</span>
                    <span className="text-forest-shadow">Hands-on organizing and system implementation</span>
                  </li>
                  <li className="flex items-start">
                    <span className="text-gold-highlight mr-2">✓</span>
                    <span className="text-forest-shadow">Product recommendations (if needed)</span>
                  </li>
                  <li className="flex items-start">
                    <span className="text-gold-highlight mr-2">✓</span>
                    <span className="text-forest-shadow">Follow-up support and maintenance tips</span>
                  </li>
                </ul>
              </div>

              {/* Process */}
              <div className="mb-6">
                <h4 className="text-lg font-semibold text-charcoal mb-3">Our Process</h4>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="text-center">
                    <div className="w-12 h-12 bg-sage-light/20 rounded-full flex items-center justify-center mx-auto mb-2">
                      <span className="text-xl font-bold text-sage-light">1</span>
                    </div>
                    <h5 className="font-medium text-charcoal mb-1">Assess</h5>
                    <p className="text-sm text-forest-shadow">Evaluate your space and understand your goals</p>
                  </div>
                  <div className="text-center">
                    <div className="w-12 h-12 bg-sage-light/20 rounded-full flex items-center justify-center mx-auto mb-2">
                      <span className="text-xl font-bold text-sage-light">2</span>
                    </div>
                    <h5 className="font-medium text-charcoal mb-1">Organize</h5>
                    <p className="text-sm text-forest-shadow">Sort, declutter, and create functional systems</p>
                  </div>
                  <div className="text-center">
                    <div className="w-12 h-12 bg-sage-light/20 rounded-full flex items-center justify-center mx-auto mb-2">
                      <span className="text-xl font-bold text-sage-light">3</span>
                    </div>
                    <h5 className="font-medium text-charcoal mb-1">Maintain</h5>
                    <p className="text-sm text-forest-shadow">Teach you how to keep your space organized</p>
                  </div>
                </div>
              </div>

              {/* CTA */}
              <div className="mt-8 pt-6 border-t border-gray-100">
                <div className="flex flex-col sm:flex-row gap-4 justify-between items-center">
                  <p className="text-forest-shadow">
                    Ready to transform your {service.title.toLowerCase()}?
                  </p>
                  <a
                    href="#contact"
                    onClick={() => setIsExpanded(false)}
                    className="px-6 py-3 bg-gold-highlight text-white font-semibold rounded-lg hover:bg-gold-highlight/90 transition-all duration-300 transform hover:scale-105"
                  >
                    Book This Service
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

// Service data
const residentialServices = [
  {
    title: 'Pantry Organization',
    description:
      'Product sourcing, workflow optimization, declutter food, boxes, add containers',
    icon: '🥫',
  },
  {
    title: 'Kitchen Organization',
    description: 'Cabinets, drawers, under-sink storage optimization',
    icon: '🍴',
  },
  {
    title: 'Closet Makeovers',
    description: 'Sorting, folding, seasonal rotation, space-saving strategies',
    icon: '👔',
  },
  {
    title: 'Home Office Setup',
    description: 'Desks, bookcases, staging, décor, paper filing systems',
    icon: '💼',
  },
  {
    title: 'Playroom Organization',
    description: 'Toy rotation systems, labeled bins for easy cleanup',
    icon: '🧸',
  },
  {
    title: 'Laundry Room Setup',
    description: 'Efficient layout and storage solutions',
    icon: '🧺',
  },
  {
    title: 'Moving Services',
    description: 'Declutter before moving and organize in new home',
    icon: '📦',
  },
  {
    title: 'Home Styling',
    description: 'Staging and declutter after organizing',
    icon: '🏡',
  },
  {
    title: 'Maintenance Plans',
    description: 'Monthly or seasonal refresh sessions',
    icon: '📅',
  },
]

const commercialServices = [
  {
    title: 'Office Space Organization',
    description: 'Desks, common areas, conference rooms optimization',
    icon: '🏢',
  },
  {
    title: 'Filing Organization',
    description: 'Physical document organization systems',
    icon: '📁',
  },
  {
    title: 'Supply Room Setup',
    description: 'Inventory and labeling systems',
    icon: '📋',
  },
  {
    title: 'Break Room Organization',
    description: 'Snacks, kitchenware, shared storage solutions',
    icon: '☕',
  },
  {
    title: 'Reception & Lobby',
    description: 'First impression-ready space decluttering',
    icon: '🎯',
  },
  {
    title: 'Workflow Optimization',
    description: 'Functional setup of high-use areas',
    icon: '⚡',
  },
  {
    title: 'Event Prep Support',
    description: 'Organizing supplies/materials before corporate events',
    icon: '🎉',
  },
]

const addOnServices = [
  {
    title: 'Donation Coordination',
    description: 'Drop-off or pickup of unwanted items',
    icon: '♻️',
  },
  {
    title: 'Labeling Services',
    description: 'Custom, aesthetic labeling for jars, bins, files',
    icon: '🏷️',
  },
  {
    title: 'Product Sourcing',
    description: 'Recommending or purchasing containers and tools',
    icon: '🛍️',
  },
  {
    title: 'Virtual Organizing',
    description: 'Guidance via video calls for remote clients',
    icon: '💻',
  },
  {
    title: 'Gift Certificates',
    description: 'Organization as a thoughtful service gift',
    icon: '🎁',
  },
  {
    title: 'Workshops & Coaching',
    description: 'Teach clients how to maintain their spaces',
    icon: '🎓',
  },
]

// Main Services Component
const Services: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false)
  const [activeTab, setActiveTab] = useState<'all' | 'residential' | 'commercial' | 'addon'>('all')

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true)
        }
      },
      { threshold: 0.1 }
    )

    const section = document.getElementById('services')
    if (section) {
      observer.observe(section)
    }

    return () => {
      if (section) {
        observer.unobserve(section)
      }
    }
  }, [])

  return (
    <section id="services" className="section-padding bg-fog-gray">
      <div className="container">
        <div
          className={`transition-all duration-1000 transform ${
            isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
          }`}>

          {/* Section Header */}
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl lg:text-5xl font-serif text-charcoal mb-4">
              Our Services
            </h2>
            <p className="text-lg text-forest-shadow max-w-3xl mx-auto">
              From residential sanctuaries to professional workspaces, we create
              organized environments that inspire productivity and peace.
            </p>
          </div>

          {/* Service Category Tabs */}
          <div className="flex flex-wrap justify-center gap-2 mb-12">
            <button
              onClick={() => setActiveTab('all')}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${
                activeTab === 'all'
                  ? 'bg-gold-highlight text-white shadow-lg'
                  : 'bg-white text-charcoal hover:bg-gold-highlight/10'
              }`}
            >
              All Services
            </button>
            <button
              onClick={() => setActiveTab('residential')}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${
                activeTab === 'residential'
                  ? 'bg-sage-light text-white shadow-lg'
                  : 'bg-white text-charcoal hover:bg-sage-light/10'
              }`}
            >
              Residential
            </button>
            <button
              onClick={() => setActiveTab('commercial')}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${
                activeTab === 'commercial'
                  ? 'bg-forest-shadow text-white shadow-lg'
                  : 'bg-white text-charcoal hover:bg-forest-shadow/10'
              }`}
            >
              Commercial
            </button>
            <button
              onClick={() => setActiveTab('addon')}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${
                activeTab === 'addon'
                  ? 'bg-gold-highlight text-white shadow-lg'
                  : 'bg-white text-charcoal hover:bg-gold-highlight/10'
              }`}
            >
              Add-On Services
            </button>
          </div>

          {/* Services Grid */}
          <div className="space-y-16">
            {/* Residential Services */}
            {(activeTab === 'all' || activeTab === 'residential') && (
              <div className={`transition-all duration-500 ${activeTab === 'residential' ? '' : ''}`}>
                {activeTab === 'all' && (
                  <h3 className="text-2xl font-serif text-charcoal mb-8 text-center flex items-center justify-center">
                    <span className="bg-sage-light/20 text-sage-light px-4 py-2 rounded-full text-sm font-medium mr-3">
                      RESIDENTIAL
                    </span>
                    Transform Your Home
                  </h3>
                )}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {residentialServices.map((service, index) => (
                    <ServiceCard 
                      key={index} 
                      service={service} 
                      index={index} 
                      category="residential"
                    />
                  ))}
                </div>
              </div>
            )}

            {/* Commercial Services */}
            {(activeTab === 'all' || activeTab === 'commercial') && (
              <div className={`transition-all duration-500 ${activeTab === 'commercial' ? '' : ''}`}>
                {activeTab === 'all' && (
                  <h3 className="text-2xl font-serif text-charcoal mb-8 text-center flex items-center justify-center">
                    <span className="bg-forest-shadow/20 text-forest-shadow px-4 py-2 rounded-full text-sm font-medium mr-3">
                      COMMERCIAL
                    </span>
                    Optimize Your Workspace
                  </h3>
                )}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {commercialServices.map((service, index) => (
                    <ServiceCard
                      key={index}
                      service={service}
                      index={activeTab === 'commercial' ? index : index + residentialServices.length}
                      category="commercial"
                    />
                  ))}
                </div>
              </div>
            )}

            {/* Add-On Services */}
            {(activeTab === 'all' || activeTab === 'addon') && (
              <div className={`transition-all duration-500 ${activeTab === 'addon' ? '' : ''}`}>
                {activeTab === 'all' && (
                  <h3 className="text-2xl font-serif text-charcoal mb-8 text-center flex items-center justify-center">
                    <span className="bg-gold-highlight/20 text-gold-highlight px-4 py-2 rounded-full text-sm font-medium mr-3">
                      ADD-ONS
                    </span>
                    Enhance Your Experience
                  </h3>
                )}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {addOnServices.map((service, index) => (
                    <ServiceCard
                      key={index}
                      service={service}
                      index={
                        activeTab === 'addon' 
                          ? index 
                          : index + residentialServices.length + commercialServices.length
                      }
                      category="addon"
                    />
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Call to Action */}
          <div className="mt-20 text-center bg-white rounded-2xl shadow-xl p-12">
            <h3 className="text-2xl font-serif text-charcoal mb-4">
              Not Sure Which Service You Need?
            </h3>
            <p className="text-lg text-forest-shadow mb-8 max-w-2xl mx-auto">
              Every space is unique. Let's discuss your specific needs and create a 
              custom organization plan that works for your lifestyle and budget.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <a
                href="#contact"
                className="inline-flex items-center justify-center px-8 py-4 bg-gold-highlight text-white font-semibold rounded-lg 
                         hover:bg-gold-highlight/90 transform hover:scale-105 transition-all duration-300 
                         shadow-lg hover:shadow-xl">
                <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M2 3a1 1 0 011-1h2.153a1 1 0 01.986.836l.74 4.435a1 1 0 01-.54 1.06l-1.548.773a11.037 11.037 0 006.105 6.105l.774-1.548a1 1 0 011.059-.54l4.435.74a1 1 0 01.836.986V17a1 1 0 01-1 1h-2C7.82 18 2 12.18 2 5V3z" />
                </svg>
                Schedule Free Consultation
              </a>
              <a
                href="tel:+16123084781"
                className="inline-flex items-center justify-center px-8 py-4 bg-white text-charcoal font-semibold rounded-lg 
                         border-2 border-charcoal hover:bg-charcoal hover:text-white
                         transform hover:scale-105 transition-all duration-300">
                Call (612) 308-4781
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
