// src/components/sections/Services.tsx
import React, { useEffect, useState, useRef } from 'react'

// Service data with enhanced details
const residentialServices = [
  {
    title: 'Pantry Organization',
    description: 'Product sourcing, workflow optimization, declutter food, boxes, add containers',
    icon: '🥫',
    duration: '3-5 hours',
    popular: true,
  },
  {
    title: 'Kitchen Organization',
    description: 'Cabinets, drawers, under-sink storage optimization',
    icon: '🍴',
    duration: '4-6 hours',
  },
  {
    title: 'Closet Makeovers',
    description: 'Sorting, folding, seasonal rotation, space-saving strategies',
    icon: '👔',
    duration: '4-8 hours',
    popular: true,
  },
  {
    title: 'Home Office Setup',
    description: 'Desks, bookcases, staging, décor, paper filing systems',
    icon: '💼',
    duration: '3-5 hours',
  },
  {
    title: 'Playroom Organization',
    description: 'Toy rotation systems, labeled bins for easy cleanup',
    icon: '🧸',
    duration: '3-4 hours',
  },
  {
    title: 'Laundry Room Setup',
    description: 'Efficient layout and storage solutions',
    icon: '🧺',
    duration: '2-3 hours',
  },
  {
    title: 'Moving Services',
    description: 'Declutter before moving and organize in new home',
    icon: '📦',
    duration: 'Custom',
    popular: true,
  },
  {
    title: 'Home Styling',
    description: 'Staging and declutter after organizing',
    icon: '🏡',
    duration: '2-4 hours',
  },
  {
    title: 'Maintenance Plans',
    description: 'Monthly or seasonal refresh sessions',
    icon: '📅',
    duration: 'Ongoing',
  },
]

const commercialServices = [
  {
    title: 'Office Space Organization',
    description: 'Desks, common areas, conference rooms optimization',
    icon: '🏢',
    duration: '6-8 hours',
  },
  {
    title: 'Filing Organization',
    description: 'Physical document organization systems',
    icon: '📁',
    duration: '4-6 hours',
  },
  {
    title: 'Supply Room Setup',
    description: 'Inventory and labeling systems',
    icon: '📋',
    duration: '3-5 hours',
  },
  {
    title: 'Break Room Organization',
    description: 'Snacks, kitchenware, shared storage solutions',
    icon: '☕',
    duration: '2-3 hours',
  },
  {
    title: 'Reception & Lobby',
    description: 'First impression-ready space decluttering',
    icon: '🎯',
    duration: '3-4 hours',
  },
  {
    title: 'Workflow Optimization',
    description: 'Functional setup of high-use areas',
    icon: '⚡',
    duration: 'Custom',
  },
  {
    title: 'Event Prep Support',
    description: 'Organizing supplies/materials before corporate events',
    icon: '🎉',
    duration: '2-4 hours',
  },
]

const addOnServices = [
  {
    title: 'Donation Coordination',
    description: 'Drop-off or pickup of unwanted items',
    icon: '♻️',
    duration: '1-2 hours',
  },
  {
    title: 'Labeling Services',
    description: 'Custom, aesthetic labeling for jars, bins, files',
    icon: '🏷️',
    duration: '2-3 hours',
  },
  {
    title: 'Product Sourcing',
    description: 'Recommending or purchasing containers and tools',
    icon: '🛍️',
    duration: 'Varies',
  },
  {
    title: 'Virtual Organizing',
    description: 'Guidance via video calls for remote clients',
    icon: '💻',
    duration: '1-2 hours',
  },
  {
    title: 'Gift Certificates',
    description: 'Organization as a thoughtful service gift',
    icon: '🎁',
    duration: 'Custom',
  },
  {
    title: 'Workshops & Coaching',
    description: 'Teach clients how to maintain their spaces',
    icon: '🎓',
    duration: '2-3 hours',
  },
]

// Enhanced Service Card Component
interface ServiceCardProps {
  service: {
    title: string;
    description: string;
    icon?: string;
    duration?: string;
    popular?: boolean;
  };
  index: number;
  category: 'residential' | 'commercial' | 'addon';
  isVisible: boolean;
}

const ServiceCard: React.FC<ServiceCardProps> = ({ service, index, category, isVisible }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);
  const cardRef = useRef<HTMLDivElement>(null);

  // Parallax effect on scroll
  useEffect(() => {
    const handleScroll = () => {
      if (cardRef.current && isVisible) {
        const scrolled = window.pageYOffset;
        const yPos = -(scrolled * 0.05) + (index * 2);
        cardRef.current.style.transform = `translateY(${yPos}px)`;
      }
    };

    if (isVisible) {
      window.addEventListener('scroll', handleScroll, { passive: true });
      return () => window.removeEventListener('scroll', handleScroll);
    }
  }, [isVisible, index]);

  // Category colors
  const categoryColors = {
    residential: {
      bg: 'bg-gradient-to-br from-sage-light/10 to-mint-accent/10',
      border: 'border-sage-light',
      icon: 'bg-gradient-to-br from-sage-light/20 to-mint-accent/20',
      text: 'text-sage-light',
      shadow: 'hover:shadow-sage-light/20'
    },
    commercial: {
      bg: 'bg-gradient-to-br from-forest-shadow/10 to-charcoal/10',
      border: 'border-forest-shadow',
      icon: 'bg-gradient-to-br from-forest-shadow/20 to-charcoal/20',
      text: 'text-forest-shadow',
      shadow: 'hover:shadow-forest-shadow/20'
    },
    addon: {
      bg: 'bg-gradient-to-br from-gold-highlight/10 to-warm-ivory/10',
      border: 'border-gold-highlight',
      icon: 'bg-gradient-to-br from-gold-highlight/20 to-warm-ivory/20',
      text: 'text-gold-highlight',
      shadow: 'hover:shadow-gold-highlight/20'
    }
  };

  const colors = categoryColors[category];

  return (
    <>
      <div 
        ref={cardRef}
        className={`relative bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-700 overflow-hidden cursor-pointer transform ${colors.shadow} ${
          isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
        }`}
        style={{ 
          transitionDelay: `${index * 100}ms`,
        }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onClick={() => setIsExpanded(true)}
      >
        {/* Popular Badge */}
        {service.popular && (
          <div className="absolute -top-2 -right-2 z-10">
            <div className="bg-gold-highlight text-white text-xs font-bold px-3 py-1 rounded-full shadow-lg transform rotate-12">
              Popular
            </div>
          </div>
        )}

        {/* Background Pattern */}
        <div className={`absolute inset-0 ${colors.bg} opacity-50`} />

        {/* Card Content */}
        <div className="relative p-8">
          {/* Icon */}
          {service.icon && (
            <div className={`w-20 h-20 ${colors.icon} rounded-2xl flex items-center justify-center mb-6 mx-auto transform transition-all duration-500 ${
              isHovered ? 'scale-110 rotate-3 shadow-lg' : ''
            }`}>
              <span className="text-4xl filter drop-shadow-md">{service.icon}</span>
            </div>
          )}

          {/* Title */}
          <h4 className="text-xl font-semibold text-charcoal mb-3 text-center">
            {service.title}
          </h4>

          {/* Description */}
          <p className="text-forest-shadow/80 text-center text-sm leading-relaxed mb-4">
            {service.description}
          </p>

          {/* Duration */}
          {service.duration && (
            <div className="text-center mb-4">
              <span className="inline-flex items-center text-xs font-medium text-charcoal/60 bg-fog-gray px-3 py-1 rounded-full">
                <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
                </svg>
                {service.duration}
              </span>
            </div>
          )}

          {/* CTA */}
          <div className={`text-center transform transition-all duration-500 ${
            isHovered ? 'translate-y-0 opacity-100' : 'translate-y-2 opacity-0'
          }`}>
            <span className={`inline-flex items-center text-sm font-semibold ${colors.text}`}>
              View Details
              <svg className="ml-1 w-4 h-4 transition-transform duration-300 group-hover:translate-x-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
              </svg>
            </span>
          </div>
        </div>

        {/* Hover Glow Effect */}
        <div className={`absolute inset-0 bg-gradient-to-t from-white/0 via-white/5 to-white/0 opacity-0 transition-opacity duration-500 ${
          isHovered ? 'opacity-100' : ''
        }`} />
      </div>

      {/* Expanded Modal */}
      {isExpanded && (
        <div 
          className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50 flex items-center justify-center p-4"
          onClick={() => setIsExpanded(false)}
        >
          <div 
            className="bg-white rounded-3xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-hidden transform scale-95 opacity-0 animate-scaleIn"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Modal Header with Gradient */}
            <div className={`relative ${colors.bg} p-10`}>
              <button
                onClick={() => setIsExpanded(false)}
                className="absolute top-4 right-4 p-2 bg-white/80 backdrop-blur-sm rounded-full text-charcoal/60 hover:text-charcoal hover:bg-white transition-all duration-300"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>

              <div className="text-center">
                {service.icon && (
                  <div className={`w-24 h-24 ${colors.icon} rounded-3xl flex items-center justify-center mx-auto mb-4 shadow-xl`}>
                    <span className="text-5xl">{service.icon}</span>
                  </div>
                )}
                <h3 className="text-3xl font-serif text-charcoal mb-2">{service.title}</h3>
                <div className="flex items-center justify-center space-x-4 text-sm">
                  <span className={`font-medium ${colors.text} capitalize`}>{category} Service</span>
                  {service.duration && (
                    <>
                      <span className="text-charcoal/30">•</span>
                      <span className="text-charcoal/60">{service.duration}</span>
                    </>
                  )}
                </div>
              </div>
            </div>

            {/* Modal Content with Smooth Scroll */}
            <div className="p-10 overflow-y-auto max-h-[60vh] scroll-smooth">
              <div className="space-y-8">
                {/* Overview */}
                <div>
                  <h4 className="text-xl font-semibold text-charcoal mb-3 flex items-center">
                    <span className="w-8 h-8 bg-sage-light/20 rounded-lg flex items-center justify-center mr-3">
                      <svg className="w-4 h-4 text-sage-light" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                      </svg>
                    </span>
                    Service Overview
                  </h4>
                  <p className="text-forest-shadow leading-relaxed pl-11">{service.description}</p>
                </div>

                {/* What's Included */}
                <div>
                  <h4 className="text-xl font-semibold text-charcoal mb-4 flex items-center">
                    <span className="w-8 h-8 bg-mint-accent/20 rounded-lg flex items-center justify-center mr-3">
                      <svg className="w-4 h-4 text-mint-accent" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    </span>
                    What's Included
                  </h4>
                  <ul className="space-y-3 pl-11">
                    {[
                      'Initial consultation and space assessment',
                      'Custom organization plan tailored to your needs',
                      'Hands-on organizing and system implementation',
                      'Product recommendations and shopping list',
                      'Follow-up support and maintenance guide'
                    ].map((item, idx) => (
                      <li key={idx} className="flex items-start">
                        <svg className="w-5 h-5 text-mint-accent mr-2 mt-0.5 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                        </svg>
                        <span className="text-forest-shadow">{item}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Process Timeline */}
                <div>
                  <h4 className="text-xl font-semibold text-charcoal mb-6 flex items-center">
                    <span className="w-8 h-8 bg-gold-highlight/20 rounded-lg flex items-center justify-center mr-3">
                      <svg className="w-4 h-4 text-gold-highlight" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M10 2a6 6 0 00-6 6v3.586l-.707.707A1 1 0 004 14h12a1 1 0 00.707-1.707L16 11.586V8a6 6 0 00-6-6zM10 18a3 3 0 01-3-3h6a3 3 0 01-3 3z" />
                      </svg>
                    </span>
                    Our Process
                  </h4>
                  <div className="relative pl-11">
                    {/* Timeline Line */}
                    <div className="absolute left-16 top-0 bottom-0 w-0.5 bg-gradient-to-b from-sage-light via-mint-accent to-gold-highlight"></div>

                    {/* Timeline Steps */}
                    <div className="space-y-8">
                      {[
                        { step: 'Assess', desc: 'Evaluate your space and understand your goals' },
                        { step: 'Plan', desc: 'Create a customized organization strategy' },
                        { step: 'Organize', desc: 'Sort, declutter, and implement systems' },
                        { step: 'Optimize', desc: 'Fine-tune for maximum efficiency' },
                        { step: 'Maintain', desc: 'Teach you how to keep your space organized' }
                      ].map((item, idx) => (
                        <div key={idx} className="relative flex items-start">
                          <div className="absolute left-5 w-10 h-10 bg-white border-4 border-sage-light rounded-full flex items-center justify-center font-bold text-sage-light shadow-md">
                            {idx + 1}
                          </div>
                          <div className="ml-20">
                            <h5 className="font-semibold text-charcoal mb-1">{item.step}</h5>
                            <p className="text-sm text-forest-shadow">{item.desc}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* CTA Section */}
              <div className="mt-12 p-6 bg-gradient-to-r from-sage-light/10 to-mint-accent/10 rounded-2xl">
                <div className="flex flex-col sm:flex-row gap-4 items-center justify-between">
                  <div>
                    <p className="text-charcoal font-semibold mb-1">
                      Ready to transform your {service.title.toLowerCase()}?
                    </p>
                    <p className="text-sm text-forest-shadow">
                      Book now and get 10% off your first service
                    </p>
                  </div>
                  <a
                    href="#contact"
                    onClick={() => setIsExpanded(false)}
                    className="px-8 py-3 bg-gold-highlight text-white font-semibold rounded-lg hover:bg-gold-highlight/90 transition-all duration-300 transform hover:scale-105 shadow-lg whitespace-nowrap"
                  >
                    Book This Service
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

// Main Services Component with Enhanced Scrolling
const Services: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'all' | 'residential' | 'commercial' | 'addon'>('all');
  const [visibleCards, setVisibleCards] = useState<Set<number>>(new Set());
  const sectionRef = useRef<HTMLDivElement>(null);
  const headerRef = useRef<HTMLDivElement>(null);
  const [isHeaderSticky, setIsHeaderSticky] = useState(false);

  // Intersection Observer for card animations
  useEffect(() => {
    const observers: IntersectionObserver[] = [];
    const cards = document.querySelectorAll('.service-card-wrapper');

    cards.forEach((card, index) => {
      const observer = new IntersectionObserver(
        ([entry]) => {
          if (entry.isIntersecting) {
            setVisibleCards(prev => new Set(prev).add(index));
          }
        },
        { threshold: 0.1, rootMargin: '50px' }
      );

      observer.observe(card);
      observers.push(observer);
    });

    return () => {
      observers.forEach(observer => observer.disconnect());
    };
  }, [activeTab]);

  // Sticky header effect
  useEffect(() => {
    const handleScroll = () => {
      if (headerRef.current && sectionRef.current) {
        const headerBottom = headerRef.current.offsetTop + headerRef.current.offsetHeight;
        setIsHeaderSticky(window.scrollY > headerBottom - 100);
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Filter services based on active tab
  const getFilteredServices = () => {
    switch (activeTab) {
      case 'residential':
        return { residential: residentialServices };
      case 'commercial':
        return { commercial: commercialServices };
      case 'addon':
        return { addon: addOnServices };
      default:
        return {
          residential: residentialServices,
          commercial: commercialServices,
          addon: addOnServices
        };
    }
  };

  const filteredServices = getFilteredServices();
  let cardIndex = 0;

  return (
    <section ref={sectionRef} id="services" className="section-padding bg-gradient-to-b from-fog-gray via-white to-warm-ivory overflow-hidden">
      <div className="container">
        {/* Section Header */}
        <div ref={headerRef} className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-serif text-charcoal mb-6 opacity-0 animate-fadeInUp">
            Our Services
          </h2>
          <p className="text-lg md:text-xl text-forest-shadow max-w-3xl mx-auto opacity-0 animate-fadeInUp animation-delay-200">
            From residential sanctuaries to professional workspaces, we create
            organized environments that inspire productivity and peace.
          </p>
        </div>

        {/* Sticky Tab Navigation */}
        <div className={`transition-all duration-500 ${
          isHeaderSticky ? 'fixed top-20 left-0 right-0 z-40 bg-white/95 backdrop-blur-md shadow-lg py-4' : 'mb-12'
        }`}>
          <div className={isHeaderSticky ? 'container' : ''}>
            <div className="flex flex-wrap justify-center gap-2">
              {[
                { id: 'all', label: 'All Services', bgActive: 'bg-gold-highlight', bgHover: 'hover:bg-gold-highlight/10' },
                { id: 'residential', label: 'Residential', bgActive: 'bg-sage-light', bgHover: 'hover:bg-sage-light/10' },
                { id: 'commercial', label: 'Commercial', bgActive: 'bg-forest-shadow', bgHover: 'hover:bg-forest-shadow/10' },
                { id: 'addon', label: 'Add-Ons', bgActive: 'bg-mint-accent', bgHover: 'hover:bg-mint-accent/10' }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => {
                    setActiveTab(tab.id as any);
                    setVisibleCards(new Set());
                  }}
                  className={`px-6 md:px-8 py-3 rounded-full font-medium transition-all duration-300 transform hover:scale-105 ${
                    activeTab === tab.id
                      ? `${tab.bgActive} text-white shadow-lg`
                      : `bg-white text-charcoal ${tab.bgHover} shadow-md`
                  }`}
                >
                  {tab.label}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Add spacer when sticky */}
        {isHeaderSticky && <div className="h-20"></div>}

        {/* Services Grid with Smooth Transitions */}
        <div className="space-y-20">
          {/* Residential Services */}
          {filteredServices.residential && (
            <div className="opacity-0 animate-fadeIn animation-delay-300">
              {activeTab === 'all' && (
                <div className="text-center mb-10">
                  <span className="inline-flex items-center px-6 py-2 bg-sage-light/20 text-sage-light rounded-full text-sm font-bold uppercase tracking-wider mb-4">
                    Residential
                  </span>
                  <h3 className="text-2xl md:text-3xl font-serif text-charcoal">Transform Your Home</h3>
                </div>
              )}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
                {filteredServices.residential.map((service, index) => (
                  <div key={index} className="service-card-wrapper">
                    <ServiceCard 
                      service={service} 
                      index={cardIndex++} 
                      category="residential"
                      isVisible={visibleCards.has(cardIndex - 1)}
                    />
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Commercial Services */}
          {filteredServices.commercial && (
            <div className="opacity-0 animate-fadeIn animation-delay-400">
              {activeTab === 'all' && (
                <div className="text-center mb-10">
                  <span className="inline-flex items-center px-6 py-2 bg-forest-shadow/20 text-forest-shadow rounded-full text-sm font-bold uppercase tracking-wider mb-4">
                    Commercial
                  </span>
                  <h3 className="text-2xl md:text-3xl font-serif text-charcoal">Optimize Your Workspace</h3>
                </div>
              )}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
                {filteredServices.commercial.map((service, index) => (
                  <div key={index} className="service-card-wrapper">
                    <ServiceCard
                      service={service}
                      index={cardIndex++}
                      category="commercial"
                      isVisible={visibleCards.has(cardIndex - 1)}
                    />
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Add-On Services */}
          {filteredServices.addon && (
            <div className="opacity-0 animate-fadeIn animation-delay-500">
