// src/components/sections/Services.tsx
import React, { useEffect, useState } from 'react'

// Service data
const residentialServices = [
  {
    title: 'Pantry Organization',
    description:
      'Product sourcing, workflow optimization, declutter food, boxes, add containers',
    icon: '🥫',
  },
  {
    title: 'Kitchen Organization',
    description: 'Cabinets, drawers, under-sink storage optimization',
    icon: '🍴',
  },
  {
    title: 'Closet Makeovers',
    description: 'Sorting, folding, seasonal rotation, space-saving strategies',
    icon: '👔',
  },
  {
    title: 'Home Office Setup',
    description: 'Desks, bookcases, staging, décor, paper filing systems',
    icon: '💼',
  },
  {
    title: 'Playroom Organization',
    description: 'Toy rotation systems, labeled bins for easy cleanup',
    icon: '🧸',
  },
  {
    title: 'Laundry Room Setup',
    description: 'Efficient layout and storage solutions',
    icon: '🧺',
  },
  {
    title: 'Moving Services',
    description: 'Declutter before moving and organize in new home',
    icon: '📦',
  },
  {
    title: 'Home Styling',
    description: 'Staging and declutter after organizing',
    icon: '🏡',
  },
  {
    title: 'Maintenance Plans',
    description: 'Monthly or seasonal refresh sessions',
    icon: '📅',
  },
]

const commercialServices = [
  {
    title: 'Office Space Organization',
    description: 'Desks, common areas, conference rooms optimization',
    icon: '🏢',
  },
  {
    title: 'Filing Organization',
    description: 'Physical document organization systems',
    icon: '📁',
  },
  {
    title: 'Supply Room Setup',
    description: 'Inventory and labeling systems',
    icon: '📋',
  },
  {
    title: 'Break Room Organization',
    description: 'Snacks, kitchenware, shared storage solutions',
    icon: '☕',
  },
  {
    title: 'Reception & Lobby',
    description: 'First impression-ready space decluttering',
    icon: '🎯',
  },
  {
    title: 'Workflow Optimization',
    description: 'Functional setup of high-use areas',
    icon: '⚡',
  },
  {
    title: 'Event Prep Support',
    description: 'Organizing supplies/materials before corporate events',
    icon: '🎉',
  },
]

const addOnServices = [
  {
    title: 'Donation Coordination',
    description: 'Drop-off or pickup of unwanted items',
    icon: '♻️',
  },
  {
    title: 'Labeling Services',
    description: 'Custom, aesthetic labeling for jars, bins, files',
    icon: '🏷️',
  },
  {
    title: 'Product Sourcing',
    description: 'Recommending or purchasing containers and tools',
    icon: '🛍️',
  },
  {
    title: 'Virtual Organizing',
    description: 'Guidance via video calls for remote clients',
    icon: '💻',
  },
  {
    title: 'Gift Certificates',
    description: 'Organization as a thoughtful service gift',
    icon: '🎁',
  },
  {
    title: 'Workshops & Coaching',
    description: 'Teach clients how to maintain their spaces',
    icon: '🎓',
  },
]

// Service Card Component
interface ServiceCardProps {
  service: {
    title: string
    description: string
    icon?: string
  }
  index: number
}

const ServiceCard: React.FC<ServiceCardProps> = ({ service, index }) => {
  return (
    <div
      className="bg-white rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 p-6 opacity-0 animate-fade-in cursor-pointer transform hover:-translate-y-1"
      style={{
        animationDelay: `${index * 50}ms`,
        animationFillMode: 'forwards',
      }}>
      {service.icon && (
        <div className="w-12 h-12 bg-mint-accent/20 rounded-full flex items-center justify-center mb-4 mx-auto">
          <span className="text-2xl">{service.icon}</span>
        </div>
      )}
      <h4 className="text-xl font-semibold text-charcoal mb-3 text-center">
        {service.title}
      </h4>
      <p className="text-forest-shadow/80 text-center text-sm">
        {service.description}
      </p>
    </div>
  )
}

// Main Services Component
const Services: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false)

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true)
        }
      },
      { threshold: 0.1 }
    )

    const section = document.getElementById('services')
    if (section) {
      observer.observe(section)
    }

    return () => {
      if (section) {
        observer.unobserve(section)
      }
    }
  }, [])

  return (
    <section id="services" className="py-20 bg-fog-gray">
      <div className="container mx-auto px-4">
        <div
          className={`transition-all duration-1000 transform ${
            isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
          }`}>
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-serif text-charcoal mb-4">
              Our Services
            </h2>
            <p className="text-lg text-forest-shadow max-w-2xl mx-auto">
              From residential sanctuaries to professional workspaces, we create
              organized environments that inspire productivity and peace.
            </p>
          </div>

          {/* Residential Services */}
          <div className="mb-16">
            <h3 className="text-2xl font-serif text-charcoal mb-8 text-center">
              Residential Services
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {residentialServices.map((service, index) => (
                <ServiceCard key={index} service={service} index={index} />
              ))}
            </div>
          </div>

          {/* Commercial Services */}
          <div className="mb-16">
            <h3 className="text-2xl font-serif text-charcoal mb-8 text-center">
              Commercial Services
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {commercialServices.map((service, index) => (
                <ServiceCard
                  key={index}
                  service={service}
                  index={index + residentialServices.length}
                />
              ))}
            </div>
          </div>

          {/* Add-On Services */}
          <div>
            <h3 className="text-2xl font-serif text-charcoal mb-8 text-center">
              Add-On Services
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {addOnServices.map((service, index) => (
                <ServiceCard
                  key={index}
                  service={service}
                  index={
                    index +
                    residentialServices.length +
                    commercialServices.length
                  }
                />
              ))}
            </div>
          </div>

          {/* Call to Action */}
          <div className="mt-16 text-center">
            <p className="text-lg text-forest-shadow mb-6">
              Not sure which service you need? Let's discuss your space and
              create a custom plan.
            </p>
            <a
              href="#contact"
              className="inline-block px-8 py-4 bg-gold-highlight text-white font-semibold rounded-lg 
                       hover:bg-gold-highlight/90 transform hover:scale-105 transition-all duration-300 
                       shadow-lg hover:shadow-xl">
              Get a Free Consultation
            </a>
          </div>
        </div>
      </div>
    </section>
  )
}

export default Services
