// src/components/sections/Hero.tsx
import React, { useEffect, useState, useRef } from 'react';
import heroImage from '../../assets/images/hero-organized.png';

const Hero: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const parallaxRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    setIsVisible(true);

    // Preload image
    const img = new Image();
    img.src = heroImage;
    img.onload = () => setImageLoaded(true);
  }, []);

  // Parallax effect
  useEffect(() => {
    let ticking = false;

    const updateParallax = () => {
      if (parallaxRef.current) {
        const scrolled = window.pageYOffset;
        parallaxRef.current.style.transform = `translateY(${scrolled * 0.5}px)`;
      }
      ticking = false;
    };

    const handleScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(updateParallax);
        ticking = true;
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const offset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - offset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <section id="hero" className="relative h-screen min-h-[700px] flex items-center justify-center overflow-hidden bg-charcoal">
      {/* Loading Placeholder */}
      {!imageLoaded && (
        <div className="absolute inset-0 bg-gradient-to-br from-forest-shadow to-sage-light animate-pulse" />
      )}

      {/* Background Image with Parallax */}
      <div 
        ref={parallaxRef}
        className={`absolute inset-0 w-full h-[120%] bg-cover bg-center bg-no-repeat transition-opacity duration-1000 ${
          imageLoaded ? 'opacity-100' : 'opacity-0'
        }`}
        style={{
          backgroundImage: `url(${heroImage})`,
          willChange: 'transform'
        }}
        aria-hidden="true"
      />

      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-charcoal/90 via-charcoal/70 to-transparent" />

      {/* Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-24 -right-24 w-96 h-96 bg-gold-highlight/10 rounded-full blur-3xl animate-float" />
        <div className="absolute -bottom-24 -left-24 w-96 h-96 bg-mint-accent/10 rounded-full blur-3xl animate-float-delayed" />
      </div>

      {/* Content */}
      <div className={`relative z-10 text-center px-6 max-w-5xl mx-auto transition-all duration-1000 transform ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}>
        {/* Badge */}
        <div className="inline-block mb-8 animate-fadeInDown animation-delay-200">
          <span className="inline-flex items-center px-5 py-2.5 bg-white/10 backdrop-blur-md rounded-full text-white/90 text-sm font-medium border border-white/20 shadow-lg">
            <span className="relative flex h-2 w-2 mr-3">
              <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-gold-highlight opacity-75"></span>
              <span className="relative inline-flex rounded-full h-2 w-2 bg-gold-highlight"></span>
            </span>
            Now Serving Twin Cities Metro
          </span>
        </div>

        {/* Title with Word Animation */}
        <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-serif text-white mb-8 leading-tight">
          {['Transform', 'Clutter', 'into', 'Clarity'].map((word, index) => (
            <span
              key={word}
              className="inline-block animate-fadeInUp mr-2 md:mr-4"
              style={{ animationDelay: `${(index + 2) * 150}ms` }}
            >
              {word}
            </span>
          ))}
        </h1>

        {/* Subtitle */}
        <p className="text-base sm:text-lg md:text-xl lg:text-2xl text-gray-100 mb-12 max-w-3xl mx-auto leading-relaxed animate-fadeInUp animation-delay-700">
          Luxury organization services tailored to your lifestyle. Create spaces that inspire productivity and peace.
        </p>

        {/* CTA Buttons */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center items-center animate-fadeInUp animation-delay-900">
          <button
            onClick={() => scrollToSection('contact')}
            className="group relative inline-flex items-center px-10 py-4 bg-gold-highlight text-white font-semibold 
                     rounded-lg transform transition-all duration-300 overflow-hidden
                     shadow-lg hover:shadow-2xl hover:scale-105"
          >
            <span className="absolute inset-0 bg-white/20 transform -translate-x-full group-hover:translate-x-0 transition-transform duration-500"></span>
            <span className="relative flex items-center">
              Book a Consultation
              <svg
                className="w-5 h-5 ml-2 group-hover:translate-x-1 transition-transform duration-300"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
              </svg>
            </span>
          </button>

          <button
            onClick={() => scrollToSection('services')}
            className="group relative inline-flex items-center px-10 py-4 text-white font-semibold 
                     rounded-lg transform transition-all duration-300 overflow-hidden
                     border border-white/30 hover:border-white/50"
          >
            <span className="absolute inset-0 bg-white/10 backdrop-blur-md"></span>
            <span className="absolute inset-0 bg-white/20 transform scale-x-0 group-hover:scale-x-100 transition-transform duration-500 origin-left"></span>
            <span className="relative">View Services</span>
          </button>
        </div>

        {/* Trust Indicators */}
        <div className="mt-20 grid grid-cols-1 sm:grid-cols-3 gap-8 max-w-3xl mx-auto animate-fadeInUp animation-delay-1000">
          {[
            { icon: 'â­', value: '5.0', label: 'Client Rating' },
            { icon: 'ðŸ‘¥', value: '50+', label: 'Happy Clients' },
            { icon: 'ðŸŽ“', value: '10+', label: 'Years Experience' }
          ].map((item, index) => (
            <div 
              key={item.label}
              className="group flex flex-col items-center text-white/90 transform transition-all duration-300 hover:scale-110"
              style={{ animationDelay: `${1100 + index * 100}ms` }}
            >
              <div className="relative w-14 h-14 bg-white/10 backdrop-blur-md rounded-full flex items-center justify-center mb-3 group-hover:bg-white/20 transition-colors">
                <span className="text-2xl">{item.icon}</span>
              </div>
              <span className="text-3xl font-bold mb-1">{item.value}</span>
              <span className="text-sm opacity-80">{item.label}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Scroll Indicator */}
      <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 animate-fadeIn animation-delay-1000">
        <button
          onClick={() => scrollToSection('about')}
          className="group flex flex-col items-center text-white/60 hover:text-white transition-all duration-300"
          aria-label="Scroll to next section"
        >
          <span className="text-xs uppercase tracking-wider mb-3 transform group-hover:-translate-y-1 transition-transform">
            Scroll to explore
          </span>
          <div className="relative w-7 h-12">
            <div className="absolute inset-0 border-2 border-white/30 rounded-full group-hover:border-white/50 transition-colors"></div>
            <div className="absolute top-2 left-1/2 transform -translate-x-1/2 w-1.5 h-4 bg-white/60 rounded-full animate-bounce group-hover:bg-white transition-colors"></div>
          </div>
        </button>
      </div>
    </section>
  );
};

export default Hero;
