// src/components/sections/Services.tsx
import React, { useEffect, useState, useRef } from 'react'

// Service data
const services = {
  residential: [
    {
      title: 'Pantry Organization',
      description: 'Transform your pantry with smart storage solutions and intuitive organization systems',
      icon: '🥫',
    },
    {
      title: 'Kitchen Systems',
      description: 'Optimize cabinets, drawers, and countertops for effortless meal preparation',
      icon: '🍴',
    },
    {
      title: 'Closet Design',
      description: 'Create a wardrobe system that makes getting dressed a joy',
      icon: '👔',
    },
    {
      title: 'Home Office',
      description: 'Design a workspace that inspires productivity and creativity',
      icon: '💼',
    },
    {
      title: 'Living Spaces',
      description: 'Bring harmony to playrooms, bedrooms, and common areas',
      icon: '🏡',
    },
    {
      title: 'Moving Support',
      description: 'Seamless organization before, during, and after your move',
      icon: '📦',
    },
  ],
  commercial: [
    {
      title: 'Office Spaces',
      description: 'Create efficient workflows with strategic space planning',
      icon: '🏢',
    },
    {
      title: 'Filing Systems',
      description: 'Implement intuitive document organization for easy retrieval',
      icon: '📁',
    },
    {
      title: 'Supply Management',
      description: 'Streamline inventory with smart labeling and storage',
      icon: '📋',
    },
    {
      title: 'Common Areas',
      description: 'Design inviting spaces that foster collaboration',
      icon: '☕',
    },
  ],
  addons: [
    {
      title: 'Virtual Consulting',
      description: 'Expert guidance through video consultations',
      icon: '💻',
    },
    {
      title: 'Maintenance Plans',
      description: 'Regular refresh sessions to maintain your organized spaces',
      icon: '📅',
    },
  ]
}

// Minimalist Service Card
interface ServiceCardProps {
  service: {
    title: string
    description: string
    icon?: string
  }
  index: number
  isVisible: boolean
}

const ServiceCard: React.FC<ServiceCardProps> = ({ service, index, isVisible }) => {
  return (
    <div 
      className={`group relative bg-white rounded-2xl p-8 transition-all duration-700 hover:shadow-xl
                  ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}
      style={{ 
        transitionDelay: `${index * 100}ms`
      }}
    >
      {/* Subtle gradient background on hover */}
      <div className="absolute inset-0 bg-gradient-to-br from-sage-light/5 to-mint-accent/5 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500" />

      <div className="relative">
        {/* Icon */}
        {service.icon && (
          <span className="text-4xl block mb-6">{service.icon}</span>
        )}

        {/* Title */}
        <h4 className="text-xl font-medium text-charcoal mb-3 group-hover:text-forest-shadow transition-colors duration-300">
          {service.title}
        </h4>

        {/* Description */}
        <p className="text-sm text-forest-shadow/70 leading-relaxed">
          {service.description}
        </p>

        {/* Subtle arrow indicator */}
        <div className="mt-6 text-gold-highlight opacity-0 group-hover:opacity-100 transform translate-x-0 group-hover:translate-x-2 transition-all duration-300">
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M17 8l4 4m0 0l-4 4m4-4H3" />
          </svg>
        </div>
      </div>
    </div>
  )
}

// Category Section with Horizontal Scroll
interface CategorySectionProps {
  title: string
  subtitle: string
  services: any[]
  isVisible: boolean
}

const CategorySection: React.FC<CategorySectionProps> = ({ title, subtitle, services, isVisible }) => {
  const scrollRef = useRef<HTMLDivElement>(null)
  const [showLeftArrow, setShowLeftArrow] = useState(false)
  const [showRightArrow, setShowRightArrow] = useState(true)

  const handleScroll = () => {
    if (scrollRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = scrollRef.current
      setShowLeftArrow(scrollLeft > 0)
      setShowRightArrow(scrollLeft < scrollWidth - clientWidth - 10)
    }
  }

  const scroll = (direction: 'left' | 'right') => {
    if (scrollRef.current) {
      const scrollAmount = 320 // Card width + gap
      scrollRef.current.scrollBy({
        left: direction === 'left' ? -scrollAmount : scrollAmount,
        behavior: 'smooth'
      })
    }
  }

  useEffect(() => {
    const element = scrollRef.current
    if (element) {
      element.addEventListener('scroll', handleScroll)
      handleScroll() // Check initial state
      return () => element.removeEventListener('scroll', handleScroll)
    }
  }, [])

  return (
    <div className={`mb-20 transition-all duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      {/* Section Header */}
      <div className="mb-8">
        <h3 className="text-2xl font-light text-charcoal mb-2">{title}</h3>
        <p className="text-sm text-forest-shadow/60">{subtitle}</p>
      </div>

      {/* Horizontal Scroll Container */}
      <div className="relative">
        {/* Left Arrow */}
        <button
          onClick={() => scroll('left')}
          className={`absolute left-0 top-1/2 -translate-y-1/2 z-10 w-12 h-12 bg-white/90 backdrop-blur-sm rounded-full shadow-lg flex items-center justify-center transition-all duration-300 hover:bg-white ${
            showLeftArrow ? 'opacity-100 -translate-x-6' : 'opacity-0 translate-x-0 pointer-events-none'
          }`}
        >
          <svg className="w-5 h-5 text-charcoal" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        {/* Right Arrow */}
        <button
          onClick={() => scroll('right')}
          className={`absolute right-0 top-1/2 -translate-y-1/2 z-10 w-12 h-12 bg-white/90 backdrop-blur-sm rounded-full shadow-lg flex items-center justify-center transition-all duration-300 hover:bg-white ${
            showRightArrow ? 'opacity-100 translate-x-6' : 'opacity-0 translate-x-0 pointer-events-none'
          }`}
        >
          <svg className="w-5 h-5 text-charcoal" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>

        {/* Cards Container */}
        <div 
          ref={scrollRef}
          className="flex gap-6 overflow-x-auto scrollbar-hide scroll-smooth pb-4"
          style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
        >
          {services.map((service, index) => (
            <div key={index} className="flex-none w-80">
              <ServiceCard service={service} index={index} isVisible={isVisible} />
            </div>
          ))}
          {/* Extra padding at the end */}
          <div className="flex-none w-6" />
        </div>
      </div>
    </div>
  )
}

// Main Services Component
const Services: React.FC = () => {
  const [sectionsVisible, setSectionsVisible] = useState({
    header: false,
    residential: false,
    commercial: false,
    addons: false,
    cta: false
  })

  useEffect(() => {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    }

    const observers = {
      header: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) {
          setSectionsVisible(prev => ({ ...prev, header: true }))
        }
      }, observerOptions),
      residential: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) {
          setSectionsVisible(prev => ({ ...prev, residential: true }))
        }
      }, observerOptions),
      commercial: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) {
          setSectionsVisible(prev => ({ ...prev, commercial: true }))
        }
      }, observerOptions),
      addons: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) {
          setSectionsVisible(prev => ({ ...prev, addons: true }))
        }
      }, observerOptions),
      cta: new IntersectionObserver(([entry]) => {
        if (entry.isIntersecting) {
          setSectionsVisible(prev => ({ ...prev, cta: true }))
        }
      }, observerOptions)
    }

    // Observe elements
    const headerEl = document.getElementById('services-header')
    const residentialEl = document.getElementById('services-residential')
    const commercialEl = document.getElementById('services-commercial')
    const addonsEl = document.getElementById('services-addons')
    const ctaEl = document.getElementById('services-cta')

    if (headerEl) observers.header.observe(headerEl)
    if (residentialEl) observers.residential.observe(residentialEl)
    if (commercialEl) observers.commercial.observe(commercialEl)
    if (addonsEl) observers.addons.observe(addonsEl)
    if (ctaEl) observers.cta.observe(ctaEl)

    return () => {
      Object.values(observers).forEach(observer => observer.disconnect())
    }
  }, [])

  return (
    <section id="services" className="py-24 bg-fog-gray/30">
      <div className="container">
        {/* Minimalist Header */}
        <div 
          id="services-header"
          className={`max-w-2xl mb-20 transition-all duration-1000 ${
            sectionsVisible.header ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
          }`}
        >
          <h2 className="text-4xl md:text-5xl font-light text-charcoal mb-6">
            Services
          </h2>
          <p className="text-lg text-forest-shadow/70 leading-relaxed">
            We believe in the transformative power of organization. Every space 
            has potential — we help you realize it.
          </p>
        </div>

        {/* Service Categories */}
        <div id="services-residential">
          <CategorySection
            title="Residential"
            subtitle="Transform your home into a sanctuary of order and beauty"
            services={services.residential}
            isVisible={sectionsVisible.residential}
          />
        </div>

        <div id="services-commercial">
          <CategorySection
            title="Commercial"
            subtitle="Elevate your workspace for enhanced productivity"
            services={services.commercial}
            isVisible={sectionsVisible.commercial}
          />
        </div>

        <div id="services-addons">
          <CategorySection
            title="Additional Services"
            subtitle="Flexible solutions to support your organizational journey"
            services={services.addons}
            isVisible={sectionsVisible.addons}
          />
        </div>

        {/* Elegant CTA */}
        <div 
          id="services-cta"
          className={`mt-32 text-center transition-all duration-1000 ${
            sectionsVisible.cta ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
          }`}
        >
          <div className="max-w-2xl mx-auto">
            <p className="text-lg text-forest-shadow/70 mb-8">
              Every journey begins with a single step. Let's take it together.
            </p>
            <a
              href="#contact"
              className="inline-flex items-center px-8 py-4 text-charcoal border border-charcoal/20 rounded-full
                       hover:bg-charcoal hover:text-white transition-all duration-500 group"
            >
              <span>Start Your Transformation</span>
              <svg className="ml-3 w-5 h-5 transform group-hover:translate-x-1 transition-transform duration-300" 
                   fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M17 8l4 4m0 0l-4 4m4-4H3" />
              </svg>
            </a>
          </div>
        </div>
      </div>
    </section>
  )
}

